      renderButton() {
        if(this.props.isRemoval === true){
          return <button className="Track-action">-</button>;
        }else{
          return <button className="Track-action">+</button>;
        }
        }




      render(){
         return (
           <div className="Track">
             <div className="Track-information">
               <h3>{this.props.name}</h3>
               <p>{this.props.artist} | {this.props.album}</p>
             </div>
             {this.renderButton()}
           </div>

         );
      }
  }

export default Track;
